import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './manual.css';

const stylesList = [
    { id: 'photographic', name: 'Фотография', img: '/images/photographic.png', tooltip: 'Яркие цвета, упрощённые формы, динамичный мультяшный стиль.' },
    { id: 'anime', name: 'Аниме', img: '/images/anime.png', tooltip: 'Стиль японской анимации с выразительными глазами и детализированными персонажами.' },
    { id: 'comic-book', name: 'Комикс', img: '/images/comic.png', tooltip: 'Импрессионизм, мазки кисти и живописные текстуры как у Ван Гога.' },
];

export default function Manual() {
    const [style, setStyle] = useState(null);
    const [loading, setLoading] = useState(false);
    const [question1, setQuestion1] = useState('');
    const [question2, setQuestion2] = useState('');
    const [question3, setQuestion3] = useState('');
    const [numImages, setNumImages] = useState(4);
    const [isAuthorized, setIsAuthorized] = useState(null);
    const [showModal, setShowModal] = useState(false);

    const modalRef = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetch('http://storysight.ru/api/auth/me', {
            credentials: 'include',
        })
            .then(res => res.ok ? res.json() : Promise.reject())
            .then(() => setIsAuthorized(true))
            .catch(() => {
                setIsAuthorized(false);
                setShowModal(true);
            });
    }, []);

    useEffect(() => {
        if (showModal) {
            const script = document.createElement('script');
            script.src = 'https://telegram.org/js/telegram-widget.js?22';
            script.setAttribute('data-telegram-login', 'zavod_worker_bot'); 
            script.setAttribute('data-size', 'large');
            script.setAttribute('data-userpic', 'false');
            script.setAttribute('data-onauth', 'onTelegramAuth(user)');
            script.setAttribute('data-request-access', 'write');
            script.async = true;

            if (modalRef.current) {
                modalRef.current.innerHTML = '';
                modalRef.current.appendChild(script);
            }

            window.onTelegramAuth = function (user) {
                fetch('http://storysight.ru/api/auth/login', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    credentials: 'include',
                    body: JSON.stringify(user),
                })
                    .then(res => {
                        if (res.ok) {
                            setIsAuthorized(true);
                            setShowModal(false);
                        }
                    })
                    .catch(err => console.error('Auth error', err));
            };
        }
    }, [showModal]);

    const handleGenerate = async () => {
        if (!style || !question1.trim() || !question2.trim() || !question3.trim()) {
            alert('Заполните все поля и выберите стиль');
            return;
        }

        setLoading(true);

        try {
            const res = await fetch('http://storysight.ru/api/generate-manual', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include',
                body: JSON.stringify({
                    style,
                    question1,
                    question2,
                    question3,
                    num_images: numImages,
                }),
            });

            const data = await res.json();

            if (res.ok) {
                const images = data.images.map(img => `data:image/webp;base64,${img}`);
                navigate('/result-main', {
                    state: {
                        images,
                        storyline: data.storyline,
                        prompts: data.prompts,
                    },
                });
            } else {
                alert(data.error || 'Ошибка при генерации');
            }
        } catch (err) {
            console.error(err);
            alert('Ошибка соединения');
        } finally {
            setLoading(false);
        }
    };

    if (isAuthorized === null) return <div className="manual-container">Загрузка...</div>;

    return (
        <div className="manual-container">
            <h1 className="manual-title">Ручной ввод идеи</h1>

            <label className="manual-label">Выберите стиль:</label>
            <div className="styles-grid">
                {stylesList.map(({ id, name, img, tooltip }) => (
                    <div
                        key={id}
                        className={`style-box flip-card ${style === id ? 'selected' : ''}`}
                        onClick={() => setStyle(id)}
                        title={tooltip}
                    >
                        <div className="flip-card-inner">
                            <div className="flip-card-front">
                                <img src={img} alt={name} className="style-image" />
                                <div className="style-name">{name}</div>
                            </div>
                            <div className="flip-card-back">
                                <p className="style-description">{tooltip}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            <div className="manual-questions">
                <label className="manual-label">1. Каким вы видите своего персонажа?</label>
                <textarea
                    className="manual-textarea"
                    value={question1}
                    onChange={(e) => setQuestion1(e.target.value)}
                />

                <label className="manual-label">2. Чем вы занимаетесь или какой продукт/товар создаёте?</label>
                <textarea
                    className="manual-textarea"
                    value={question2}
                    onChange={(e) => setQuestion2(e.target.value)}
                />

                <label className="manual-label">3. Кратко опишите сюжет вашей истории</label>
                <textarea
                    className="manual-textarea"
                    value={question3}
                    onChange={(e) => setQuestion3(e.target.value)}
                />
            </div>

            <div className="manual-slider-block">
                <label className="manual-label">
                    Количество изображений: {numImages}
                </label>
                <input
                    type="range"
                    min={1}
                    max={8}
                    value={numImages}
                    onChange={(e) => setNumImages(Number(e.target.value))}
                    className="manual-slider"
                />
            </div>

            <div className="button-row">
                <button className="btn btn-gray" onClick={() => navigate('/create-choice')}>Назад</button>
                <button className="btn btn-purple" onClick={handleGenerate} disabled={loading}>
                    {loading ? 'Генерация...' : 'Сгенерировать'}
                </button>
            </div>

            {showModal && (
                <div className="modal-overlay">
                    <div className="modal">
                        <h2>Вход через Telegram</h2>
                        <p>Пожалуйста, войдите, чтобы продолжить</p>
                        <div ref={modalRef}></div>
                    </div>
                </div>
            )}
        </div>
    );
}
