import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import Home from "./pages/Home";
import MyProjects from "./pages/myprojects";
import CreateChoice from "./pages/CreateChoice";
import GenerateCharacter from "./pages/GenerateCharacter";
import Template from "./pages/Template";
import Manual from "./pages/manual";
import TelegramLogin from "./pages/TelegramLogin";
import ResultMain from "./pages/ResultMain";

import RequireAuth from "./pages/RequireAuth";

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />

        {/* Эти страницы доступны ТОЛЬКО если пользователь авторизован */}
        <Route
          path="/create-choice"
          element={
            <RequireAuth>
              <CreateChoice />
            </RequireAuth>
          }
        />
        <Route
          path="/generate-character"
          element={
            <RequireAuth>
              <GenerateCharacter />
            </RequireAuth>
          }
        />
        <Route
          path="/template"
          element={
            <RequireAuth>
              <Template />
            </RequireAuth>
          }
        />
        <Route
          path="/manual"
          element={
            <RequireAuth>
              <Manual />
            </RequireAuth>
          }
        />
        <Route
          path="/result-main"
          element={
            <RequireAuth>
              <ResultMain />
            </RequireAuth>
          }
        />
        <Route
          path="/my-projects"
          element={
            <RequireAuth>
              <MyProjects />
            </RequireAuth>
          }
        />

        {/* Страница авторизации */}
        <Route path="/login" element={<TelegramLogin />} />
      </Routes>
    </Router>
  );
};

export default App;
