// src/pages/myprojects.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const MyProjects = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get(" http://localhost:3000/my-projects")
      .then((res) => {
        setImages(res.data);
      })
      .catch((err) => {
        console.error("Ошибка при загрузке изображений:", err);
      })
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="min-h-screen px-6 py-10 bg-gradient-to-br from-pink-200 to-purple-300">
      <h1 className="text-4xl font-bold text-center mb-8 text-gray-800">Мои проекты</h1>
      {loading ? (
        <p className="text-center text-gray-600">Загрузка...</p>
      ) : images.length === 0 ? (
        <p className="text-center text-gray-600">Пока нет ни одного изображения</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {images.map((img) => (
              <div key={img.id} className="bg-white rounded-2xl shadow-lg p-4 flex flex-col items-center">
                <img
                    src={img.url}
                    alt={img.title || "Изображение"}
                    className="rounded-xl mb-4 max-h-64 object-contain"
                />
                {img.title && (
                    <textarea
                        readOnly
                        value={img.title}
                        className="w-full text-sm text-gray-800 border rounded-lg p-2 resize-none bg-gray-50"
                    />
                )}
              </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyProjects;
