// src/pages/myprojects.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./myprojects.css";

const MyProjects = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
        .get("http://localhost:3000/my-projects")
        .then((res) => {
          setImages(res.data);
        })
        .catch((err) => {
          console.error("Ошибка при загрузке изображений:", err);
        })
        .finally(() => setLoading(false));
  }, []);

  // Функция скачивания картинки по base64
  const downloadImage = (base64data, title, id) => {
    const link = document.createElement("a");
    link.href = base64data;
    const safeTitle = title ? title.replace(/[^a-z0-9]/gi, '_').toLowerCase() : `image_${id}`;
    link.download = `${safeTitle}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
      <div className="myprojects-container">
        <h1 className="myprojects-title">Мои проекты</h1>
        {loading ? (
            <p className="myprojects-message">Загрузка...</p>
        ) : images.length === 0 ? (
            <p className="myprojects-message">Пока нет ни одного изображения</p>
        ) : (
            <div className="myprojects-grid">
              {images.map((img) => (
                  <div key={img.id} className="project-card">
                    <img
                        src={img.url}
                        alt={img.title || "Изображение"}
                        className="project-image"
                    />
                    {img.title && (
                        <div className="project-prompt">{img.title}</div>
                    )}
                    <button
                        className="download-button"
                        onClick={() => downloadImage(img.url, img.title, img.id)}
                    >
                      Скачать
                    </button>
                  </div>
              ))}
            </div>
        )}
      </div>
  );
};

export default MyProjects;
