import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function ResultMain() {
    const location = useLocation();
    const navigate = useNavigate();

    const images = location.state?.images || [];
    const storyline = location.state?.storyline || '';
    const prompts = location.state?.prompts || []; // список текстов для каждой картинки

    if (images.length === 0) {
        return (
            <div className="p-4 text-center">
                <h2 className="text-xl mb-4">Нет изображений для отображения</h2>
                <button
                    onClick={() => navigate('/')}
                    className="px-4 py-2 rounded-xl bg-black text-white hover:bg-gray-800"
                >
                    На главную
                </button>
            </div>
        );
    }

    const handleDownload = (url, index) => {
        const link = document.createElement('a');
        link.href = url;
        link.download = `comic_scene_${index + 1}.png`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    const handleSaveProject = () => {
        // Заглушка — позже заменим на POST-запрос
        const projectData = {
            storyline,
            prompts,
            images,
        };

        console.log('Сохраняем проект:', projectData);
        alert('Функция сохранения пока не реализована.');
    };

    return (
        <div className="max-w-5xl mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4 text-center">Ваш комикс</h1>

            <div className="bg-gray-100 p-4 rounded-xl shadow mb-6">
                <h2 className="text-lg font-semibold mb-2">Сюжет:</h2>
                <p className="text-gray-700 whitespace-pre-line">{storyline}</p>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
                {images.map((url, i) => (
                    <div key={i} className="bg-white rounded-xl shadow p-3">
                        <img
                            src={url}
                            alt={`Scene ${i + 1}`}
                            className="rounded-xl w-full h-auto mb-2"
                        />
                        <p className="text-sm text-gray-600 mb-2">{prompts[i]}</p>
                        <button
                            onClick={() => handleDownload(url, i)}
                            className="w-full py-2 bg-black text-white rounded-xl hover:bg-gray-800"
                        >
                            Скачать
                        </button>
                    </div>
                ))}
            </div>

            <div className="flex flex-col sm:flex-row justify-center gap-4">
                <button
                    onClick={() => navigate('/')}
                    className="px-6 py-2 bg-black text-white rounded-xl hover:bg-gray-800"
                >
                    На главную
                </button>
                <button
                    onClick={() => navigate(-1)}
                    className="px-6 py-2 bg-gray-300 rounded-xl hover:bg-gray-400"
                >
                    Назад
                </button>
                <button
                    onClick={handleSaveProject}
                    className="px-6 py-2 bg-indigo-600 text-white rounded-xl hover:bg-indigo-700"
                >
                    Сохранить в мои проекты
                </button>
            </div>
        </div>
    );
}
