import React, { useState } from 'react';
import './Template.css';
import { useNavigate } from 'react-router-dom';

export default function Template() {
    const [storyText, setStoryText] = useState('');
    const [selectedCharacter, setSelectedCharacter] = useState(null);
    const [numImages, setNumImages] = useState(4); // –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é 4
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    const characterTemplates = [/* ...—Ç–≤–æ–π –º–∞—Å—Å–∏–≤ –ø–µ—Ä—Å–æ–Ω–∞–∂–µ–π –±–µ–∑ –∏–∑–º–µ–Ω–µ–Ω–∏–π... */];

    const handleSubmit = async () => {
        if (!storyText.trim()) {
            setErrorMessage('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ —Å—é–∂–µ—Ç.');
            return;
        }
        if (!selectedCharacter) {
            setErrorMessage('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –ø–µ—Ä—Å–æ–Ω–∞–∂–∞.');
            return;
        }

        setErrorMessage('');

        try {
            const response = await fetch('http://localhost:8000/generate-comic', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    prompt: storyText,
                    character_name: selectedCharacter,
                    num_images: numImages, // üëà —Ç–µ–ø–µ—Ä—å –ø–µ—Ä–µ–¥–∞—ë–º –∑–Ω–∞—á–µ–Ω–∏–µ –ø–æ–ª–∑—É–Ω–∫–∞
                }),
            });

            if (!response.ok) {
                throw new Error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ.');
            }

            const data = await response.json();
            const imageBase64List = data.images.map((img) => `data:image/png;base64,${img}`);
            const finalStoryline = data.storyline;
            const promptsList = data.prompts;

            navigate('/result-main', {
                state: {
                    images: imageBase64List,
                    storyline: finalStoryline,
                    prompts: promptsList
                }
            });
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞:', error);
            setErrorMessage('–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∏ –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.');
        }
    };

    return (
        <div className="choise-wrapper">
            <h1 className="choise-heading">–°–æ–∑–¥–∞–π—Ç–µ —Å–≤–æ–π —Å—Ç–æ—Ä–∏—Ç–µ–ª–ª–∏–Ω–≥</h1>

            <textarea
                className="story-textarea"
                placeholder="–ù–∞–ø–∏—à–∏—Ç–µ –∑–¥–µ—Å—å –Ω–µ–±–æ–ª—å—à–æ–π —Å—é–∂–µ—Ç –≤–∞—à–µ–π –∏—Å—Ç–æ—Ä–∏–∏..."
                value={storyText}
                onChange={(e) => setStoryText(e.target.value)}
            />

            <div className="character-grid">
                {characterTemplates.map((template) => (
                    <div
                        key={template.id}
                        className={`character-card-container ${selectedCharacter === template.id ? 'selected' : ''}`}
                        onClick={() => setSelectedCharacter(template.id)}
                    >
                        <div className="character-card-inner">
                            <div className="character-card-front">
                                <img
                                    src={`/images/${template.id}.jpg`}
                                    alt={template.title}
                                    className="character-img"
                                />
                                <h3 className="character-title">{template.title}</h3>
                            </div>
                            <div className="character-card-back">
                                <p>{template.description}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* –ü–æ–ª–∑—É–Ω–æ–∫ –≤—ã–±–æ—Ä–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π */}
            <div className="slider-container">
                <label htmlFor="numImages" className="slider-label">
                    –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π: {numImages}
                </label>
                <input
                    id="numImages"
                    type="range"
                    min="1"
                    max="8"
                    value={numImages}
                    onChange={(e) => setNumImages(Number(e.target.value))}
                    className="slider"
                />
            </div>

            {errorMessage && <div className="error-message">{errorMessage}</div>}

            <button className="generate-btn" onClick={handleSubmit}>
                –°–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å –∫–æ–º–∏–∫—Å
            </button>

            <button className="back-btn" onClick={() => navigate("/create-choice")}>
                –ù–∞–∑–∞–¥
            </button>
        </div>
    );
}
