import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";

const works = [
  { id: 1, image: "/images/work1.png", description: "Ваша чистота - ваше спокойствие" },
  { id: 2, image: "/images/work2.png", description: "Продавец шаурмы, который старается привлечь клиентов" },
  { id: 3, image: "/images/work3.png", description: "Магазин комнатных растений" },
  { id: 4, image: "/images/work4.png", description: "Блокнот, который меняет всё" },
  { id: 5, image: "/images/work5.png", description: "Сапожник и путь за гвоздями" },
  { id: 6, image: "/images/work6.png", description: "Маркетинг через историю героя-путешественника" },
  { id: 7, image: "/images/work7.png", description: "Владелица пекарни и неожиданный успех" },
  { id: 8, image: "/images/work8.png", description: "Умный дом — уют по щелчку" },
  { id: 9, image: "/images/work9.png", description: "Ремонт старинных часов" },
  { id: 10, image: "/images/work10.png", description: "Крафтовая упаковка подарков" },
  { id: 11, image: "/images/work11.png", description: "Пекарня — аромат и уют" },
  { id: 12, image: "/images/work12.png", description: "Воспоминания из одного путешествия" },
  { id: 13, image: "/images/work13.png", description: "Продавец машин — контраст «до и после»" },
  { id: 14, image: "/images/work14.png", description: "Онлайн-курс по мышлению" },
];

const Home = () => {
  const navigate = useNavigate();
  const telegramRef = useRef(null);
  const [user, setUser] = useState(null);

  // Callback, который вызовет Telegram Login Widget при успешной авторизации
  window.TelegramLoginWidgetCallback = async function (userData) {
    console.log("Telegram login data:", userData);

    try {
      const response = await fetch("http://storysight.ru/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(userData),
      });

      if (!response.ok) throw new Error("Ошибка при авторизации");

      const data = await response.json();
      // Сохраняем данные пользователя
      setUser({
        id: userData.id,
        username: userData.username,
        first_name: userData.first_name,
        last_name: userData.last_name,
        photo_url: userData.photo_url,
      });
    } catch (error) {
      console.error("Login error:", error);
    }
  };

  // При загрузке страницы пытаемся получить сессию с сервера
  useEffect(() => {
    fetch("http://storysight.ru/auth/me", {
      credentials: "include",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Не авторизован");
        return res.json();
      })
      .then((data) => {
        setUser(data);
      })
      .catch(() => {
        setUser(null);
      });
  }, []);

  // Подключаем Telegram Login Widget без data-auth-url (чтобы не было редиректа)
  useEffect(() => {
    if (telegramRef.current && !user) {
      telegramRef.current.innerHTML = "";

      const script = document.createElement("script");
      script.src = "https://telegram.org/js/telegram-widget.js?22";
      script.async = true;
      script.setAttribute("data-telegram-login", "zavod_worker_bot");
      script.setAttribute("data-size", "large");
      script.setAttribute("data-userpic", "true");
      script.setAttribute("data-lang", "ru");
      script.setAttribute("data-request-access", "write");
      // Обработка авторизации через JS callback — **без data-auth-url!**
      script.setAttribute("data-onauth", "TelegramLoginWidgetCallback(user)");

      telegramRef.current.appendChild(script);
    }
  }, [user]);

  // Функция выхода — посылаем запрос на сервер и очищаем состояние
  const logout = async () => {
    try {
      await fetch("http://storysight.ru/auth/logout", {
        method: "POST",
        credentials: "include",
      });
    } catch (e) {
      console.error("Logout failed", e);
    }
    setUser(null);
  };

  return (
    <div className="home-container">
      <nav className="top-tabs">
        <span className="tab" onClick={() => navigate("/generate-character")}>
          Конструктор персонажа
        </span>
        <span className="tab" onClick={() => navigate("/my-projects")}>
          Мои проекты
        </span>
      </nav>

      <main className="main-content">
        <h1 className="headline">Создай свою историю</h1>
        <p className="subtitle">Сделай запоминающуюся историю для своего бизнеса за 3 шага.</p>

        <div className="button-telegram-wrapper">
          <div className="button-group">
            <button className="btn" onClick={() => navigate("/create-choice")}>
              Начать
            </button>

            {user ? (
              <div className="user-info" style={{ display: "flex", alignItems: "center", gap: 10, marginLeft: 20 }}>
                <img
                  src={user.photo_url}
                  alt={user.first_name || user.username}
                  style={{ width: 48, height: 48, borderRadius: "50%" }}
                />
                <span>{user.first_name || user.username}</span>
                <button className="btn-logout" onClick={logout} style={{ marginLeft: 10 }}>
                  Выйти
                </button>
              </div>
            ) : (
              <div className="telegram-login" ref={telegramRef}></div>
            )}
          </div>
        </div>
      </main>

      <section className="portfolio">
        {works.map((work) => (
          <div key={work.id} className="work-item">
            <img src={work.image} alt={`Work ${work.id}`} />
            <div className="work-overlay">
              <p>{work.description}</p>
            </div>
          </div>
        ))}
      </section>
    </div>
  );
};

export default Home;
