// src/pages/myprojects.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./myprojects.css";

const MyProjects = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
        .get("http://localhost:3000/my-projects")
        .then((res) => {
          setImages(res.data);
        })
        .catch((err) => {
          console.error("Ошибка при загрузке изображений:", err);
        })
        .finally(() => setLoading(false));
  }, []);

  const downloadImage = (url, title, id) => {
    const link = document.createElement("a");
    link.href = url;
    const safeTitle = title ? title.replace(/[^a-z0-9]/gi, '_').toLowerCase() : `image_${id}`;
    link.download = `${safeTitle}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Группировка по storyline + дате создания (rounded до минуты)
  const groupedImages = images.reduce((acc, img) => {
    const date = new Date(img.created_at).toISOString().slice(0, 16);
    const key = `${date}||${img.storyline || 'Без описания'}`;
    acc[key] = acc[key] || [];
    acc[key].push(img);
    return acc;
  }, {});

  // Сортировка по дате (новые сверху)
  const sortedGroups = Object.entries(groupedImages).sort((a, b) => b[0].localeCompare(a[0]));

  return (
      <div className="myprojects-container">
        <h1 className="myprojects-title">Мои проекты</h1>
        {loading ? (
            <p className="myprojects-message">Загрузка...</p>
        ) : sortedGroups.length === 0 ? (
            <p className="myprojects-message">Пока нет ни одного изображения</p>
        ) : (
            sortedGroups.map(([key, group], index) => {
              const [datetime, storyline] = key.split("||");
              return (
                  <div key={index} className="project-group">
                    <div className="storyline">{storyline}</div>
                    <div className="myprojects-grid">
                      {group.map((img) => (
                          <div key={img.id} className="project-card">
                            <img
                                src={img.url}
                                alt={img.title || "Изображение"}
                                className="project-image"
                            />
                            {img.title && (
                                <div className="project-prompt">{img.title}</div>
                            )}
                            <button
                                className="download-button"
                                onClick={() => downloadImage(img.url, img.title, img.id)}
                            >
                              Скачать
                            </button>
                          </div>
                      ))}
                    </div>
                  </div>
              );
            })
        )}
      </div>
  );
};

export default MyProjects;
